#define MAX_ROW 600#define MAX_COL 600class TextTable {	public:			unsigned long numColumns, numRows,currColumn,currRow;		Handle cells[MAX_ROW][MAX_COL];						TextTable();		~TextTable();			void AddLastRow(void);		void AddCellToLastRow(char *text);		void AddLastColumn(void);		void AddCellToLastColumn(char *text);		void SetCell(unsigned long r, unsigned long c, char *text);		void GetCell(unsigned long r, unsigned long c, char *text);		Boolean ReadTableFromFile(FILE *fp) ;		Boolean WriteTableToFile(FILE *fp) ;		void TransposeTable(void);};